// 3ra pagina creada:
import React, { useContext } from 'react';

import {
  IonButtons,
  IonContent,
  IonHeader,
  IonItem,
  IonList,
  IonMenuButton,    // crear el boton para desplegar el sidebar
  IonPage,
  IonTitle,
  IonToolbar,
} from '@ionic/react';


const TodosObjetivos: React.FC = () => {

  const objts = DETALLE_CURSOS.map(curso => {return curso.objetivos}).reduce((
    objetivoVector, objetivosNesteados) => {

      let objetivoVectorActualizado = objetivoVector;
      for (const objetivo of objetivosNesteados) {
        objetivoVectorActualizado = objetivoVectorActualizado.concat(objetivo);
      };
      return objetivoVectorActualizado;

  }, []);

  return (
    <IonPage>

      <IonHeader>
        <IonToolbar color="primary">
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>TODOS LOS OBJETIVOS</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        <IonList>
          {objts.map(obj => (
            <IonItem key={obj.id}>
              {obj.texto}
            </IonItem>
          ))} 
        </IonList>
      </IonContent>

    </IonPage>
  )
}

export default TodosObjetivos

// "IonHeader" para la toolbar y "IonContent" para el contenido de la pagina

// reduce = ejecuta una función reductora en cada elemento del array, 
//          lo que da como resultado un único valor de salida.
