// 1ra pagina creada:
import React from 'react'

import {
  IonButton,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardTitle,
  IonCol,
  IonContent,
  IonGrid,
  IonHeader,
  IonPage,
  IonRow,
  IonTitle,
  IonToolbar,
} from '@ionic/react'

// import { useHistory } from 'react-router-dom'

export const DETALLE_CURSOS = [
  {
    id: 'curso1',
    titulo: 'Vinos',
    objetivos: [
      { id: 'curso1o1', texto: 'Termina el curso de Vino!' },
      { id: 'curso1o2', texto: 'Aprende mucho de Vino.' },
    ],
  },
  {
    id: 'curso2',
    titulo: 'Cerveza',
    objetivos: [
      { id: 'curso2o1', texto: 'Termina el curso de Cerveza!' },
      { id: 'c2o2', texto: 'Aprende mucho de Cerveza.' },
    ],
  },
  {
    id: 'curso3',
    titulo: 'Vodka',
    objetivos: [
      { id: 'c3o1', texto: 'Termina el curso de Vodka!' },
      { id: 'c3o2', texto: 'Aprende mucho de Vodka.' },
    ],
  },
]

const Cursos: React.FC = () => {

  // const history = useHistory()
  // const cambiarPantallaHandler = () => {history.push('/objetivos')}

  return (
    <IonPage>

      <IonHeader>
        <IonToolbar color='primary'>
          <IonTitle>CURSOS</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        <IonGrid>

          {DETALLE_CURSOS.map((curso) => (
            <IonRow key={curso.id}>
              <IonCol size-md='4' offset-md='4'>
                <IonCard>

                  <IonCardHeader>
                    <IonCardTitle>
                      {curso.titulo}
                    </IonCardTitle>
                  </IonCardHeader>

                  <IonCardContent>
                    <div className="ion-text-right">
                      <IonButton fill="clear" color="secondary" routerLink={`/cursos/${curso.id}`}>
                        Ver Objetivos del Curso
                      </IonButton>
                    </div>
                  </IonCardContent>

                </IonCard>
              </IonCol>
            </IonRow>
          ))}

        </IonGrid>
        {/*<div>
           Mismo resultado, distinto funcionamiento

          <IonButton routerLink='/objetivos'>Ver Objetivos 1</IonButton>
          <IonButton onClick={cambiarPantallaHandler}>Ver Objetivos 2</IonButton>
        </div>
        */}
      </IonContent>

    </IonPage>
  )
}

export default Cursos

// "IonHeader" para la toolbar y "IonContent" para el contenido de la pagina

// Aqui planteo dos formas de configurar botones para movernos entre paginas:
// routerLink = indica el path (url) a seguir al hacer click (funciona como link)
// onClick = indica en este caso el cambio de pagina a traves de navegacion programada

// useHistory = permite ingresar al historial del navegador y cambiarlo. Cumple lo mismo que 
//              "routerLink" pero genera un boton en lugar de un link a url normal.

// .push = empuja un nuevo camino "path" en el historial

// size-md = alineo la colunma (max de 12) donde 4 + 4 = 8 y me quedan 2 por lado para centrar

// DETALLE_CURSOS.map() = por cada curso repetimos todo el contenido de "IonRow"

// {`/cursos/${curso.id}`} = indicamos dinamicamente el "path" al que debe ir el boton
