// 1er componente creado.
// 6to elemento general creado.
// Sacamos el elemento "IonModal" de "Objetivos.tsx"

import React, { useRef } from "react";

import {
  IonModal, // Para habilitar un pop up luego de que se cumpla una accion
  IonHeader,
  IonToolbar,
  IonTitle,
  IonContent,
  IonButton,
  IonGrid,
  IonRow,
  IonCol,
  IonLabel,
  IonItem,
  IonInput,
} from '@ionic/react';


const EdicionModal: React.FC <{

  mostrar: boolean; 
  cancelar: () => void;
  objetivoEditado: {id: string; text: string} | null;

}> = props => {

  const textoRef = useRef<HTMLiON>();

  return (
    <IonModal isOpen={props.mostrar}>

      <IonHeader>
        <IonToolbar color="primary">
          <IonTitle>{props.objetivoEditado ? "EDITAR" : "AÑADIR"} Objetivo</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        <IonGrid>
          <IonRow>
            <IonCol>
              <IonItem>
                <IonLabel position="floating">Ingresa objetivo:</IonLabel>
                <IonInput type="text" value={props.objetivoEditado?.text}/>
              </IonItem>
            </IonCol>
          </IonRow>

          <IonRow className="ion-text-center">
            <IonCol>
              <IonButton fill="clear" onClick={props.cancelar}>Cancelar</IonButton>
            </IonCol>
            <IonCol>
              <IonButton color="secondary" expand="block">Guardar</IonButton>
            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>

    </IonModal>
  )
};

export default EdicionModal


