import React from "react";

const CursosContexto = React.createContext <Contexto> ({ 
  cursos: [],
  añadirCurso: () => {},
  añadirObjetivo: () => {},
  borrarObjetivo: () => {},
  actualizarObjetivo: () => {},
  cambiarFiltroCurso: () => {}
});

interface Contexto {
  cursos: Curso[];     
  añadirCurso: (tituloCurso: string) => void;
  añadirObjetivo: (cursoId: string, textoObjetivo: string) => void;
  borrarObjetivo: (cursoId: string, objetivoId: string) => void;
  actualizarObjetivo: (cursoId: string, objetivoId: string, nuevoTexto: string) => void;
  cambiarFiltroCurso: (cursoId: string, estaIncluido: boolean) => void;
};

// (3)
 export interface Curso {
  id: string;
  titulo: string;
  objetivos: Objetivo[];   // Toma desde "interface Objetivo"
  incluido: boolean;
};

// (4)
export interface Objetivo {
  id: string;
  texto: string;
};

export default CursosContexto;



// createContext = maneja los datos que le brindemos desde distintos lugares de la app
//                 En este caso el contecto es un objeto {} con un vector de "cursos"

// cursos: {}[] = indica que el contexto maneja un objeto donde el campo de "cursos"
//                va a ser un vector de objetos.
//                Aqui estoy moviendo el {} a la interface de "Curso"

// Objetivo: {}[] = Idem para cursos

// interface = para la descripcion de tipos de variables.
