import React, { useState } from 'react'

import CursosContexto, { Curso,  } from "./CursosContexto"

const CursosContectoProveedor: React.FC = props => {

  const [cursos, setCursos] = useState <Curso[]> ([
    {
      id: "c1",
      titulo: "Making Wine!",         // Curso inicial de prueba
      objetivos: []
    }
  ]);

  // Creo funciones para cada accion que detalle en "CursosContexto"
  const añadirCurso = (titulo: string) => {

    const nuevoCurso: Curso = {
      id: new Date().toString(),
      titulo,
      objetivos: []
    };

    setCursos((actualCurso) => {
      return actualCurso.concat(nuevoCurso);
    });

  };

  const añadirObjetivo = (cursoId: string, texto: string) => {
    const nuevoObjetivo = 
    setCursos(cursos => {
      const cursosActualizados = [...cursos];
      const cursoActualizadoIndex = cursosActualizados.findIndex(curso => curso.id === cursoId);
      const objetivosActualizados = cursosActualizados[cursoActualizadoIndex].objetivos.concat()
      return [];
    });
  };

  const borrarObjetivo = () => {};

  const actualizarObjetivo = () => {};


  return (

    <CursosContexto.Provider value={{
      cursos: cursos,
      añadirCurso,
      añadirObjetivo,
      borrarObjetivo,
      actualizarObjetivo
    }}>
      {props.children}
    </CursosContexto.Provider>

  );

};

