// 2da pagina creada:
// Misma estructura que "Cursos.tsx"
import React, { useState, useRef, useContext } from 'react';

import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonButtons,
  IonBackButton,
  IonList,
  IonItem,
  IonLabel,
  IonItemSliding, // permite que todos los elementos dentro puedan deslizarce hacia los lados
  IonItemOptions,
  IonItemOption,
  IonIcon,
  IonButton,
  IonFab,
  IonFabButton,  // para botones flotantes
  isPlatform,    // habilita a que ciertos componentes solo figuren si se usa un dispositivo especifico
  IonAlert,
} from '@ionic/react';

import { useParams } from "react-router-dom"; // Permite extraer parametros obtenidos en esta pagina. 

// Importo iconos:
import { addOutline, create, trash } from 'ionicons/icons';

// Importo Componentes:
import EdicionModal from "../Componentes/EdicionModal";

// Importo Contexto
import CursosContexto from '../Datos/CursosContexto';


const Objetivos: React.FC = () => {

  const [incioBorrar, setInicioBorrar] = useState(false);
  const [editando, setEditando] = useState(false);
  const [objetivoSeleccionado, setObjetivoSeleccionado] = useState<any>(null);

  const opcionesDeslizamiento = useRef<HTMLIonItemSlidingElement>(null);

  const cursosCtx= useContext(CursosContexto);

  const idCursoSeleccionado = useParams<{ idcurso: string }>().idcurso;

  const cursoSeleccionado = cursosCtx.cursos.find(curso => curso.id === idCursoSeleccionado);

  const inicioBorrarItemObjetivo = () => {
    setInicioBorrar(true);
  };

  const borradoDefinitivo = (objetivoId: string) => {
    setInicioBorrar(false);
    cursosCtx.borrarObjetivo(idCursoSeleccionado, objetivoId);
  };

  const inicioEdicionItemObjetivo = (objetivoId: string, evento: React.MouseEvent) => {
    evento.stopPropagation();
    const objetivo = cursoSeleccionado?.objetivos.find(o => o.id === objetivoId);
    opcionesDeslizamiento.current?.closeOpened();
    if(!objetivo) {
      return;
    }
    setEditando(true);
    setObjetivoSeleccionado(objetivo);
  };

  const cancelarEdicion = () => {
    setEditando(false);
    setObjetivoSeleccionado(null);
  };

  const inicioAñadirObjetivo = () => {
    setEditando(true);
    setObjetivoSeleccionado(null);
  };

  const guardarObjetivo = (texto: string) => {
    if(objetivoSeleccionado) {
      cursosCtx.actualizarObjetivo(idCursoSeleccionado, objetivoSeleccionado.id, texto)
    } else {
      cursosCtx.añadirObjetivo(idCursoSeleccionado, texto);
    };
    setEditando(false);
  };


  return (
    <>
      <EdicionModal
        mostrar={editando}
        cancelar={cancelarEdicion}
        objetivoEditado={objetivoSeleccionado}
        guardar={guardarObjetivo}
      />

      <IonAlert
        isOpen={incioBorrar}
        header='Estas seguro de borrar?'
        message='Esta accion es irreversible'
        buttons={[
          {
            text: 'No',
            role: 'cancel',
            handler: () => {
              setInicioBorrar(false)
            },
          },
          { text: 'Si', handler: borradoDefinitivo },
        ]}
      />

      <IonPage>
        <IonHeader>
          <IonToolbar color='primary'>
            <IonButtons slot='start'>
              <IonBackButton defaultHref='/cursos/lista' />
            </IonButtons>

            <IonTitle>
              {cursoSeleccionado
                ? cursoSeleccionado.titulo
                : 'No existe el curso'}
            </IonTitle>

            {!isPlatform('android') && (
              <IonButtons slot='end'>
                <IonButton onClick={inicioAñadirObjetivo}>
                  <IonIcon slot='icon-only' icon={addOutline} />
                </IonButton>
              </IonButtons>
            )}
          </IonToolbar>
        </IonHeader>

        <IonContent>
          {cursoSeleccionado && (
            <IonList>
              {cursoSeleccionado.objetivos.map((objetivo) => (
                <IonItemSliding key={objetivo.id} ref={opcionesDeslizamiento}>
                  <IonItemOptions side='start'>
                    <IonItemOption
                      onClick={inicioBorrarItemObjetivo.bind(null, objetivo.id)}
                      color='danger'
                    >
                      <IonIcon slot='icon-only' icon={trash} />
                    </IonItemOption>
                  </IonItemOptions>

                  <IonItem lines='full'>
                    <IonLabel>{objetivo.texto}</IonLabel>
                  </IonItem>

                  <IonItemOptions side='end'>
                    <IonItemOption
                      onClick={inicioEdicionItemObjetivo.bind(
                        null,
                        objetivo.id
                      )}
                    >
                      <IonIcon slot='icon-only' icon={create} />
                    </IonItemOption>
                  </IonItemOptions>
                </IonItemSliding>
              ))}
            </IonList>
          )}

          {isPlatform('android') && (
            <IonFab horizontal='end' vertical='bottom' slot='fixed'>
              <IonFabButton color='secondary' onClick={inicioAñadirObjetivo}>
                <IonIcon icon={addOutline} />
              </IonFabButton>
            </IonFab>
          )}
        </IonContent>
      </IonPage>
    </>
  )
}

export default Objetivos;

// IonPage = necesario para componentes lugadoa a traves de routing deben estar dentro

// slot = indicar donde posicionar el "back button" dentro del toolbar 
//        (start= izquierda, end = derecha, icon-only = adapta el tamaño del icono, fixed = fijo)

// defaultHref = indica un url por default en caso de que ionic no sepa desde donde se viene

// useParams = permite extraer parametros obtenido en esta pagina. 
//             Implementado para "idcurso" en "Cursos.tsx"

// idCursoSeleccionado = traigo desde "App.tsx" el parametro "idcurso" y le indicamos 
//                       que siempre sera de tipo "string"

// cursoSeleccionado = tomamos el id del curso seleccionado y lo igualamos al {} correspondiente
//                     dentro de DETALLE_CURSOS

// {cursoSeleccionado ? cu...} = mostramos dinamicamente el titulo del curso seleccionado.

// lines = indica como deben figurar las lineas debajo de cada "objetivo" de DETALLE_CURSOS 

// button = simplemente para que el elemento se pueda clickear

// editarItemObjetivo = esto genera que si se hace click SOLO en el icono del boton
//                      se ejecute la funcion "editarItemObjetivo", es decir, 
//                      impide que esta accion se propague hacia afuera y afecte a 
//                      "borrarItemObjetivo"

// borrarItemObjetivo = se efectua cuando se hace click en cualquier lugar dentro del recuadro
//                      que contiene al "IonItem", a excepcion del icono.

// side = todo el set de "IonItemOptions" se debe mostrar en la izquierda del item (start)

// EdicionModal = le paso los props declarados en "EdicionModal" y los = a las funciones originales

// bind = agregamos una funcionalidad que se aplica cuando le doy click

// opcionesDeslizamiento = configura que cuando se selecciona para editar un objetivo,
//                         la barra deslizable vuelva a su lugar original
